import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Search, Heart, RefreshCw, Share2, Download, Moon, Sun, Plus, X, Copy, ArrowRight, Globe } from 'lucide-react';

// API service for backend communication
const API_BASE_URL = 'http://localhost:5000/api';

const QuoteApp = () => {
  const [quote, setQuote] = useState({ content: "Life isn't about finding yourself. Life is about creating yourself.", author: "George Bernard Shaw" });
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [theme, setTheme] = useState('dark');
  const [activeTab, setActiveTab] = useState('discover');
  const [favorites, setFavorites] = useState([]);
  const [isAddingQuote, setIsAddingQuote] = useState(false);
  const [categories, setCategories] = useState(['Inspiration', 'Success', 'Love', 'Wisdom', 'Motivation']);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [recommendedQuotes, setRecommendedQuotes] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [newQuote, setNewQuote] = useState({ content: '', author: '', category: '' });

  // Load categories from backend on mount
  useEffect(() => {
    fetchCategories();
    fetchRandomQuote();
    fetchRecommendedQuotes();
    // Load favorites from localStorage
    const savedFavorites = localStorage.getItem('favorites');
    if (savedFavorites) {
      setFavorites(JSON.parse(savedFavorites));
    }
  }, []);

  // Save favorites to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/categories`);
      if (response.ok) {
        const data = await response.json();
        // Extract category names from the API response
        const categoryNames = Array.isArray(data) ? 
          (typeof data[0] === 'string' ? data : data.map(cat => cat.name)) :
          ['Inspiration', 'Success', 'Love', 'Wisdom', 'Motivation'];
        setCategories(categoryNames);
      }
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const fetchRandomQuote = async () => {
    setIsLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/random-quote`);
      if (response.ok) {
        const data = await response.json();
        setQuote(data);
      }
    } catch (error) {
      console.error('Error fetching random quote:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const fetchRecommendedQuotes = async () => {
    try {
      const promises = [1, 2, 3].map(() => fetch(`${API_BASE_URL}/random-quote`).then(res => res.json()));
      const results = await Promise.all(promises);
      setRecommendedQuotes(results);
    } catch (error) {
      console.error('Error fetching recommended quotes:', error);
    }
  };

  const searchQuotes = async () => {
    if (!searchQuery.trim()) return;

    try {
      const response = await fetch(`${API_BASE_URL}/quote?author=${encodeURIComponent(searchQuery)}`);
      if (response.ok) {
        const data = await response.json();
        if (data && data.length > 0) {
          setQuote(data[0]);
          if (data.length > 1) {
            setRecommendedQuotes(data.slice(1, 4));
          }
          showToastNotification('Quote found');
        } else {
          showToastNotification('No quotes found for this author');
        }
      }
    } catch (error) {
      console.error('Error searching quotes:', error);
      showToastNotification('Error searching quotes');
    }
  };

  const addNewQuote = async () => {
    if (!newQuote.content || !newQuote.author) {
      showToastNotification('Quote and author are required');
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/add-quote`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content: newQuote.content,
          author: newQuote.author
        }),
      });

      if (response.ok) {
        const data = await response.json();
        setQuote(data);
        showToastNotification('Quote added successfully');
        setIsAddingQuote(false);
        setNewQuote({ content: '', author: '', category: '' });
      } else {
        showToastNotification('Error adding quote');
      }
    } catch (error) {
      console.error('Error adding quote:', error);
      showToastNotification('Error connecting to server');
    }
  };

  const toggleTheme = () => {
    setTheme(theme === 'dark' ? 'light' : 'dark');
  };

  const toggleFavorite = (quoteObj) => {
    const exists = favorites.some(fav => fav.content === quoteObj.content);
    if (exists) {
      setFavorites(favorites.filter(fav => fav.content !== quoteObj.content));
      showToastNotification('Removed from favorites');
    } else {
      setFavorites([...favorites, quoteObj]);
      showToastNotification('Added to favorites');
    }
  };

  const randomizeQuote = () => {
    fetchRandomQuote();
  };

  const handleCategoryClick = async (category) => {
    try {
      const response = await fetch(`${API_BASE_URL}/quotes/category/${category}`);
      if (response.ok) {
        const data = await response.json();
        if (data && data.length > 0) {
          setQuote(data[0]);
          if (data.length > 1) {
            setRecommendedQuotes(data.slice(1, 4));
          }
        }
      }
    } catch (error) {
      console.error('Error fetching quotes by category:', error);
    }
  };

  const showToastNotification = (message) => {
    setToastMessage(message);
    setShowToast(true);
    setTimeout(() => setShowToast(false), 2000);
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(`"${quote.content}" - ${quote.author}`);
    showToastNotification('Quote copied to clipboard');
  };

  const handleSearchKeyPress = (e) => {
    if (e.key === 'Enter') {
      searchQuotes();
    }
  };

  return (
    <div className={`min-h-screen flex flex-col ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
      {/* Animated Gradient Background */}
      <div className="fixed inset-0 pointer-events-none">
        <div className={`absolute inset-0 opacity-10 ${theme === 'dark' ? 'opacity-20' : 'opacity-10'}`}>
          <div className="absolute top-0 -left-4 w-72 h-72 bg-purple-300 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob"></div>
          <div className="absolute top-0 -right-4 w-72 h-72 bg-yellow-300 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-2000"></div>
          <div className="absolute -bottom-8 left-20 w-72 h-72 bg-pink-300 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-4000"></div>
        </div>
      </div>

      {/* Header */}
      <header className={`fixed w-full z-10 ${theme === 'dark' ? 'bg-gray-900/80' : 'bg-white/80'} backdrop-blur-md py-4 px-6 flex justify-between items-center`}>
        <div className="flex items-center space-x-2">
          <motion.div 
            initial={{ rotate: 0 }}
            animate={{ rotate: 360 }}
            transition={{ duration: 2, repeat: Infinity, repeatType: "loop", ease: "linear" }}
            className="h-8 w-8 rounded-full bg-gradient-to-r from-blue-500 to-purple-600"
          ></motion.div>
          <h1 className="text-xl font-bold bg-gradient-to-r from-blue-500 to-purple-600 bg-clip-text text-transparent">Quotient</h1>
        </div>

        <div className="flex items-center space-x-4">
          <button onClick={toggleTheme} className="p-2 rounded-full hover:bg-gray-800/20">
            {theme === 'dark' ? <Sun size={20} /> : <Moon size={20} />}
          </button>

          <div 
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="relative h-10 w-10 rounded-full bg-gradient-to-r from-blue-500 to-purple-600 flex items-center justify-center cursor-pointer"
          >
            <span className="text-white font-medium">JD</span>
          </div>
        </div>
      </header>

      {/* Side Navigation */}
      <motion.div 
        className={`fixed left-0 bottom-0 top-0 z-20 w-64 ${theme === 'dark' ? 'bg-gray-900/95' : 'bg-white/95'} backdrop-blur-md pt-20 px-4 shadow-xl`}
        initial={{ x: "-100%" }}
        animate={{ x: isMenuOpen ? 0 : "-100%" }}
        transition={{ type: "spring", damping: 20 }}
      >
        <button 
          onClick={() => setIsMenuOpen(false)}
          className="absolute top-6 right-4 p-1 rounded-full hover:bg-gray-800/20"
        >
          <X size={20} />
        </button>

        <div className="flex flex-col h-full">
          <div className="space-y-1">
            <button 
              onClick={() => setActiveTab('discover')}
              className={`w-full py-2.5 px-4 text-left rounded-lg flex items-center space-x-3 ${activeTab === 'discover' ? 'bg-blue-500/10 text-blue-500' : 'hover:bg-gray-800/10'}`}
            >
              <Globe size={18} />
              <span>Discover</span>
            </button>
            <button 
              onClick={() => setActiveTab('favorites')}
              className={`w-full py-2.5 px-4 text-left rounded-lg flex items-center space-x-3 ${activeTab === 'favorites' ? 'bg-blue-500/10 text-blue-500' : 'hover:bg-gray-800/10'}`}
            >
              <Heart size={18} />
              <span>Favorites</span>
            </button>
          </div>

          <div className="mt-8">
            <h3 className="text-xs uppercase text-gray-500 font-semibold mb-2 px-4">Categories</h3>
            <div className="space-y-1">
              {categories.map((category, index) => (
                <button 
                  key={index}
                  onClick={() => handleCategoryClick(category)}
                  className="w-full py-2 px-4 text-left rounded-lg hover:bg-gray-800/10 text-sm"
                >
                  {category}
                </button>
              ))}
            </div>
          </div>

          <div className="mt-auto pb-8">
            <button 
              onClick={() => setIsAddingQuote(true)}
              className="w-full py-2.5 px-4 bg-gradient-to-r from-blue-500 to-purple-600 rounded-lg text-white font-medium flex items-center justify-center space-x-2"
            >
              <Plus size={18} />
              <span>Add Quote</span>
            </button>
          </div>
        </div>
      </motion.div>

      {/* Main Content */}
      <main className="flex-grow pt-20 px-6 md:px-12 lg:px-24 max-w-7xl mx-auto w-full">
        {/* Search Bar */}
        <div className={`mt-6 flex items-center rounded-xl ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-2 shadow-lg`}>
          <Search className="ml-3 text-gray-400" size={20} />
          <input
            type="text"
            placeholder="Search quotes by author..."
            className={`flex-grow py-2 px-4 bg-transparent focus:outline-none ${theme === 'dark' ? 'placeholder-gray-500' : 'placeholder-gray-400'}`}
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            onKeyPress={handleSearchKeyPress}
          />
        </div>

        {/* Categories Scroll */}
        <div className="mt-6 flex space-x-2 overflow-x-auto pb-2 scrollbar-hide">
          {categories.slice(0, 8).map((category, index) => (
            <button
              key={index}
              onClick={() => handleCategoryClick(category)}
              className={`px-4 py-2 rounded-full text-sm font-medium whitespace-nowrap ${
                index === 0 
                  ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white' 
                  : theme === 'dark' ? 'bg-gray-800 hover:bg-gray-700' : 'bg-white hover:bg-gray-100'
              }`}
            >
              {category}
            </button>
          ))}
        </div>

        {/* Content based on active tab */}
        {activeTab === 'discover' ? (
          <>
            {/* Quote Display */}
            <AnimatePresence mode="wait">
              <motion.div
                key={quote.content}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.5 }}
                className={`mt-12 rounded-2xl ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 md:p-12 shadow-xl relative overflow-hidden`}
              >
                {isLoading && (
                  <div className="absolute inset-0 flex items-center justify-center bg-black/30 backdrop-blur-sm z-20">
                    <div className="w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
                  </div>
                )}

                {/* Decorative elements */}
                <div className="absolute -top-6 -right-6 w-24 h-24 rounded-full bg-gradient-to-r from-blue-500/20 to-purple-600/20 blur-xl"></div>
                <div className="absolute -bottom-6 -left-6 w-24 h-24 rounded-full bg-gradient-to-r from-purple-600/20 to-blue-500/20 blur-xl"></div>

                <div className="relative z-10">
                  <div className="text-5xl font-serif text-blue-500 opacity-20">"</div>
                  <p className="text-2xl md:text-3xl font-medium leading-relaxed mt-4">
                    {quote.content}
                  </p>
                  <div className="mt-6 flex items-center">
                    <div className="h-10 w-10 rounded-full bg-gradient-to-r from-blue-500 to-purple-600"></div>
                    <p className="ml-4 font-medium">{quote.author}</p>
                  </div>

                  <div className="mt-8 flex flex-wrap gap-3">
                    <motion.button 
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={randomizeQuote}
                      className="p-3 rounded-full bg-gradient-to-r from-blue-500 to-purple-600 text-white"
                    >
                      <RefreshCw size={20} />
                    </motion.button>

                    <motion.button 
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={() => toggleFavorite(quote)}
                      className={`p-3 rounded-full ${favorites.some(fav => fav.content === quote.content) ? 'bg-pink-500 text-white' : theme === 'dark' ? 'bg-gray-700 text-white' : 'bg-gray-100 text-gray-700'}`}
                    >
                      <Heart size={20} />
                    </motion.button>

                    <motion.button 
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={copyToClipboard}
                      className={`p-3 rounded-full ${theme === 'dark' ? 'bg-gray-700 text-white' : 'bg-gray-100 text-gray-700'}`}
                    >
                      <Copy size={20} />
                    </motion.button>

                    <motion.button 
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      className={`p-3 rounded-full ${theme === 'dark' ? 'bg-gray-700 text-white' : 'bg-gray-100 text-gray-700'}`}
                    >
                      <Share2 size={20} />
                    </motion.button>

                    <motion.button 
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      className={`p-3 rounded-full ${theme === 'dark' ? 'bg-gray-700 text-white' : 'bg-gray-100 text-gray-700'}`}
                    >
                      <Download size={20} />
                    </motion.button>
                  </div>
                </div>
              </motion.div>
            </AnimatePresence>

            {/* Recommended Quotes */}
            <div className="mt-12 mb-20">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-xl font-bold">You may also like</h2>
                <button className="text-blue-500 flex items-center">
                  See all <ArrowRight size={16} className="ml-1" />
                </button>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {recommendedQuotes.map((quote, index) => (
                  <motion.div
                    key={index}
                    whileHover={{ y: -5 }}
                    className={`rounded-xl ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 shadow-lg`}
                  >
                    <p className="text-lg font-medium">
                      {quote.content}
                    </p>
                    <p className="mt-4 text-sm text-gray-500">
                      {quote.author}
                    </p>
                    <div className="mt-4 flex justify-between items-center">
                      <div className="flex space-x-2">
                        <button 
                          onClick={() => toggleFavorite(quote)}
                          className={`p-2 rounded-full ${theme === 'dark' ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}`}
                        >
                          <Heart size={16} className={favorites.some(fav => fav.content === quote.content) ? 'text-pink-500' : ''} />
                        </button>
                        <button 
                          onClick={() => {
                            navigator.clipboard.writeText(`"${quote.content}" - ${quote.author}`);
                            showToastNotification('Quote copied');
                          }}
                          className={`p-2 rounded-full ${theme === 'dark' ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}`}
                        >
                          <Copy size={16} />
                        </button>
                      </div>
                      <button 
                        onClick={() => setQuote(quote)}
                        className="text-blue-500 text-sm font-medium"
                      >
                        View
                      </button>
                    </div>
                  </motion.div>
                ))}
              </div>
            </div>
          </>
        ) : (
          // Favorites Tab
          <div className="mt-8 mb-20">
            <h1 className="text-2xl font-bold mb-6">Your Favorite Quotes</h1>

            {favorites.length === 0 ? (
              <div className={`p-8 rounded-xl ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} text-center`}>
                <p className="text-gray-500">You haven't added any favorites yet.</p>
                <button 
                  onClick={() => setActiveTab('discover')}
                  className="mt-4 px-6 py-2 bg-gradient-to-r from-blue-500 to-purple-600 rounded-lg text-white font-medium"
                >
                  Discover Quotes
                </button>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {favorites.map((fav, index) => (
                  <motion.div
                    key={index}
                    whileHover={{ y: -5 }}
                    className={`rounded-xl ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 shadow-lg relative overflow-hidden`}
                  >
                    <div className="absolute -top-6 -right-6 w-24 h-24 rounded-full bg-gradient-to-r from-blue-500/10 to-purple-600/10 blur-xl"></div>

                    <p className="text-lg font-medium relative z-10">
                      {fav.content}
                    </p>
                    <p className="mt-4 text-sm text-gray-500">
                      {fav.author}
                    </p>
                    <div className="mt-4 flex justify-between items-center">
                      <div className="flex space-x-2">
                        <button 
                          onClick={() => toggleFavorite(fav)}
                          className="p-2 rounded-full text-pink-500"
                        >
                          <Heart size={16} />
                        </button>
                        <button 
                          onClick={() => {
                            navigator.clipboard.writeText(`"${fav.content}" - ${fav.author}`);
                            showToastNotification('Quote copied');
                          }}
                          className={`p-2 rounded-full ${theme === 'dark' ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}`}
                        >
                          <Copy size={16} />
                        </button>
                      </div>
                      <button 
                        onClick={() => {
                          setQuote(fav);
                          setActiveTab('discover');
                        }}
                        className="text-blue-500 text-sm font-medium"
                      >
                        View
                      </button>
                    </div>
                  </motion.div>
                ))}
              </div>
            )}
          </div>
        )}
      </main>

      {/* Add Quote Modal */}
      <AnimatePresence>
        {isAddingQuote && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-30 flex items-center justify-center bg-black/50 backdrop-blur-sm p-4"
          >
            <motion.div
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.9, opacity: 0 }}
              className={`w-full max-w-md ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-2xl shadow-2xl p-6`}
            >
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-bold">Add New Quote</h2>
                <button 
                  onClick={() => setIsAddingQuote(false)}
                  className="p-1 rounded-full hover:bg-gray-700/20"
                >
                  <X size={20} />
                </button>
              </div>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium mb-1.5 text-gray-400">Quote</label>
                  <textarea
                    className={`w-full p-3 rounded-lg ${theme === 'dark' ? 'bg-gray-700 focus:bg-gray-700/80' : 'bg-gray-100 focus:bg-white'} focus:outline-none focus:ring-2 focus:ring-blue-500`}
                    rows="4"
                    placeholder="Enter the quote text..."
                    value={newQuote.content}
                    onChange={(e) => setNewQuote({...newQuote, content: e.target.value})}
                  ></textarea>
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1.5 text-gray-400">Author</label>
                  <input
                    type="text"
                    className={`w-full p-3 rounded-lg ${theme === 'dark' ? 'bg-gray-700 focus:bg-gray-700/80' : 'bg-gray-100 focus:bg-white'} focus:outline-none focus:ring-2 focus:ring-blue-500`}
                    placeholder="Who said it?"
                    value={newQuote.author}
                    onChange={(e) => setNewQuote({...newQuote, author: e.target.value})}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1.5 text-gray-400">Category</label>
                  <select
                    className={`w-full p-3 rounded-lg ${theme === 'dark' ? 'bg-gray-700 focus:bg-gray-700/80' : 'bg-gray-100 focus:bg-white'} focus:outline-none focus:ring-2 focus:ring-blue-500`}
                    value={newQuote.category}
                    onChange={(e) => setNewQuote({...newQuote, category: e.target.value})}
                  >
                    <option value="">Select a category</option>
                    {categories.map((category, index) => (
                      <option key={index} value={category}>{category}</option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="mt-6 grid grid-cols-2 gap-4">
                <button
                  onClick={() => setIsAddingQuote(false)}
                  className={`py-2.5 rounded-lg font-medium ${theme === 'dark' ? 'bg-gray-700 hover:bg-gray-600' : 'bg-gray-200 hover:bg-gray-300'}`}
                >
                  Cancel
                </button>
                <button
                  onClick={addNewQuote}
                  className="py-2.5 bg-gradient-to-r from-blue-500 to-purple-600 rounded-lg text-white font-medium"
                >
                  Save Quote
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Toast Notification */}
      <AnimatePresence>
        {showToast && (
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 50 }}
            className="fixed bottom-6 left-1/2 transform -translate-x-1/2 z-50"
          >
            <div className="bg-gray-900 text-white px-6 py-3 rounded-full shadow-lg">
              {toastMessage}
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default QuoteApp;
